mapper.sql:
	
       	WHERE a.delYn = 'N'
       	
       		
       			AND a.title LIKE CONCAT('%',#{searchKeyword},'%')
       		        		
       		
       			AND a.content LIKE  CONCAT('%',#{searchKeyword},'%')
       		        		
       	
       	
      		AND b1.lineage LIKE CONCAT('%',LPAD(#{category}, 4, '0'),'%')
       	
       	
       		
      		AND a.startDate >= #{startDate} 
      		
       	
       	
       		
      		AND a.endDate <= #{endDate}

mapper.insert:
    	INSERT INTO tbEvent(
    		title 
    		, content
			, startDate
			, endDate
    		, viewCount 
    		, regId
    		, regIp 
    		, regDate
    		, delYn
    	)
    	VALUE(
    		#{title}
    		, #{content}
    		, #{startDate}
    		, #{endDate}
    		, 0
    		, #{regId}
    		, #{regIp}
    		, NOW()
    		, 'N'
    	)
		
	        SELECT LAST_INSERT_ID() AS seq

mapper.update:
UPDATE tbEvent SET
	title 		= #{title} 
	, content 	= #{content}
	, startDate = #{startDate}
	, endDate 	= #{endDate}
	, modId 	= #{modId}
	, modIp		= #{modIp}
	, modDate 	= NOW()
WHERE seq = #{seq}

    	
		UPDATE tbEvent SET 
    		delYn = 'Y'
    		, modId = #{modId}
    		, modIp	= #{modIp}
    		, modDate = NOW()
    	WHERE seq = #{seq}
